// This file can be used by tests to get the path of src. This makes
// it much easier to have tests work whether they are run from within their test folder, or
// from alltests.sh

/* Author: Mitchell Ludwig
    mainpath always returns the src directory
*/
var currDir = process.cwd();
for (var nextDir = currDir.split("/"); nextDir.pop() != 'src'; currDir = nextDir.join("/")){
}
exports.mainpath = currDir+"/";